var fadeStart = 1, fadeUntil = 250, fading = $(".paneOne .content-container img");

$(window).bind("scroll", function() {
    var a = $(document).scrollTop(), b = 0;
    if (a <= fadeStart) {
        b = 1;
    } else if (a <= fadeUntil) {
        b = 1 - a / fadeUntil;
    }
    fading.css("opacity", b);
});